/*
###########################################################################################################################################################
															CRIAÇÃO DA TABELA CADPAIS
###########################################################################################################################################################
*/


CREATE TABLE CADPAIS (
    CODIGO VARCHAR2(3) NOT NULL,
    DDI VARCHAR2(4) NOT NULL,
    ISO VARCHAR2(2) NOT NULL,
    ISO3 VARCHAR2(3) NOT NULL,
    NOME VARCHAR2(50) NOT NULL,
    NOMEFORMAL VARCHAR2(100) NOT NULL,
    
    CONSTRAINT PK_CADPAIS PRIMARY KEY (CODIGO),
    CONSTRAINT UN_CADPAIS_ISO UNIQUE(ISO,ISO3),
    CONSTRAINT UN_CADPAIS_NOME UNIQUE(NOME,NOMEFORMAL)
);

/* COMENTÁRIOS DE TABELAS E COLUNAS*/

COMMENT ON TABLE CADPAIS IS 'TABELA DESTINADA AO CADASTRO DE PAISES';
COMMENT ON COLUMN CADPAIS.CODIGO IS 'CODIGO INTERNACIONAL DO PAÍS (PRIMARY KEY)';
COMMENT ON COLUMN CADPAIS.ISO IS 'SIGLA EM 2 LETRAS DE CADA PAÍS';
COMMENT ON COLUMN CADPAIS.ISO3 IS 'SIGLA EM 3 LETRAS DE CADA PAÍS';
COMMENT ON COLUMN CADPAIS.NOME IS 'NOME POPULAR DO PAÍS';
COMMENT ON COLUMN CADPAIS.NOMEFORMAL IS 'NOME FORMAL DO PAÍS';

/* INSERÇÃO DOS DADOS FIXOS NA TABELA CADPAIS */


/*
###########################################################################################################################################################
															CRIAÇÃO DA TABELA CADNAVIO
###########################################################################################################################################################
*/

CREATE TABLE CADNAVIO (
    CODIGO VARCHAR2(7) NOT NULL,
    DESCRICAO VARCHAR2(120) NOT NULL,
    QTDPORAO NUMBER(1) NOT NULL,
    CODIGOPAISORIGEM VARCHAR2(3) NOT NULL,
    
    CONSTRAINT PK_CADNAVIO_CODIGO PRIMARY KEY(CODIGO),
    CONSTRAINT UN_CADNAVIO_DESCRIÇÃO UNIQUE(DESCRICAO),
    CONSTRAINT FK_CADNAVIO_CODIGOPAISORIGEM FOREIGN KEY(CODIGOPAISORIGEM) REFERENCES CADPAIS(CODIGO)
);
CREATE SEQUENCE SEQ_CODIGO_CADNAVIO
    START WITH 1
    INCREMENT BY 1
    NOCYCLE
    NOCACHE
    ; 
COMMENT ON TABLE CADNAVIO IS 'TABELA DESTINADA AO CADASTRO DOS NAVIOS';
COMMENT ON COLUMN CADNAVIO.CODIGO IS 'CODIGO DE IDENTIFICAÇÃO DO NAVIO (PRIMARY KEY)';
COMMENT ON COLUMN CADNAVIO.QTDPORAO IS 'QUANTIDADE DE PORÕES QUE O NAVIO POSSUI';
COMMENT ON COLUMN CADNAVIO.CODIGOPAISORIGEM IS 'PAÍS DE ORIGEM DO NAVIO';

