/*
###########################################################################################################################################################
															CRIAÇÃO DA TABELA EMBARQUE
###########################################################################################################################################################
*/

CREATE TABLE EMBARQUE (
    CODIGO VARCHAR2(7) NOT NULL,
    DATAINICIO DATE NOT NULL,
    DATAFIM DATE,
    STATUS CHAR(1),
    CANCELADO CHAR(1) NOT NULL,
    CODIGONAVIO VARCHAR(7) NOT NULL,
    CODIGOPAISDESTINO VARCHAR(3) NOT NULL,
    
    CONSTRAINT PK_EMBARQUE PRIMARY KEY(CODIGO),
    CONSTRAINT CK_EMBARQUE_STATUS CHECK(STATUS IN('I','P','F')),
    CONSTRAINT CK_EMBARQUE_CANCELADO CHECK(CANCELADO IN ('F','T')),
    CONSTRAINT FK_CADPAIS_CODIGO FOREIGN KEY (CODIGOPAISDESTINO) REFERENCES CADPAIS(CODIGO),
    CONSTRAINT FK_CADNAVIO_CODIGO FOREIGN KEY (CODIGONAVIO) REFERENCES CADNAVIO(CODIGO)
);
COMMENT ON TABLE EMBARQUE IS 'TABELA PRINCIPAL DO SISTEMA, ONDE SÃO REGISTRADOS TODOS OS EMBARQUES';
COMMENT ON COLUMN EMBARQUE.STATUS IS 'I = INICIADO P = PAUSADO F = FINALIZADO'; 
COMMENT ON COLUMN EMBARQUE.CANCELADO IS 'F = FALSE T = TRUE';

